trigger.body:
BEGIN
  IF AD_isTriggerEnabled()='N' THEN
    IF DELETING THEN
      RETURN OLD;
    ELSE
      RETURN NEW;
    END IF;
  END IF;

  -- Solo validar si el nuevo registro tiene 'Y'
  IF :NEW.EM_Shppws_Default_Eqx_Seg = 'Y' THEN
    -- Buscar si ya existe otro registro con 'Y'
    IF EXISTS (
      SELECT 1
      FROM shppee_new_customer_score
      WHERE EM_Shppws_Default_Eqx_Seg = 'Y'
        AND shppee_new_customer_score_id <> :NEW.shppee_new_customer_score_id
    ) THEN
      RAISE_APPLICATION_ERROR(-20000, '@shppws_msg_risk@');
    END IF;
  END IF;

  IF DELETING THEN
    RETURN OLD;
  ELSE
    RETURN NEW;
  END IF;
END SHPPWS_CHECK_DEFAULT_CUS_TRG
